import React, { useContext } from "react";
import { ApiContext } from "./context/apicontext";
const Card = () => {
  //   console.log(details.name.title);
    const { details}=useContext()
  return (
    <div id="card">
      <div id="photo">
        <img src={`${details.photo}`} alt="" />
      </div>
      <div id="text">
        <h1>
          {details.name.title +
            "." +
            " " +
            details.name.first +
            " " +
            details.name.last}
        </h1>
        <h3>
          <i class="ri-mail-fill"></i>
          {details.email}
        </h3>
        <h3></h3>;
      </div>
    </div>
  );
};

export default Card;
